    private void btn_AddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_AddActionPerformed
        if (first_name.getText().isEmpty() | nic_no.getText().isEmpty() | street_name.getText().isEmpty() | personal_city.getText().isEmpty() | credit_limit.getText().isEmpty() | credit_period.getText().isEmpty() | mobile.getText().isEmpty() | email.getText().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Please fill all the fields", "Feilds not filled", JOptionPane.INFORMATION_MESSAGE);
        } else if (email.getForeground().equals(RED)) {
            JOptionPane.showMessageDialog(this, "Please enter a correct email address", "Email not Valid", JOptionPane.INFORMATION_MESSAGE);
        } else if (mobile.getText().length() < 10) {
            JOptionPane.showMessageDialog(this, "Please enter a correct mobile no", "mobile not Valid", JOptionPane.INFORMATION_MESSAGE);
        } else if (grade.getSelectedIndex() == 0) {
            JOptionPane.showMessageDialog(this, "Please select a correct grade", "Please select a croeect grade ", JOptionPane.INFORMATION_MESSAGE);
        } else if (nic_no.getText().length() <= 10) {
            if (!(nic_no.getText().trim().matches("^[0-9]{9}[vVxX]$"))) {
                JOptionPane.showMessageDialog(this, "Please enter a correct nic no", "nic not Valid", JOptionPane.INFORMATION_MESSAGE);
            } else {
                try {
                    DB.iud("insert into customer (id_customer,fname,lname,nic,business_name,street,city,telephoneno,mobile,email,state,createby,date_created,grade) Values ('" + customerID.getText() + "','" + first_name.getText() + "','" + last_name.getText() + "','" + nic_no.getText() + "','" + business_name.getText() + "','" + street_name.getText() + "','" + personal_city.getText() + "','" + telephone.getText() + "','" + mobile.getText() + "','" + email.getText() + "','1','" + user + "','" + LocalDate.now() + "','" + grade.getSelectedItem().toString() + "')");
                    DB.iud("insert into customer_business (id_customer,street,city,credit_period,credit_limit) VALUES ('" + customerID.getText() + "','" + business_street.getText() + "','" + business_city.getText() + "','" + credit_period.getText() + "','" + credit_limit.getText() + "')");
                    new Save().setVisible(true);
                    generateID();
                    clearall();
                } catch (Exception e) {
                    e.printStackTrace();
                }
            }
        }


    }//GEN-LAST:event_btn_AddActionPerformed

