    private void mobileKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_mobileKeyTyped
        Validation.setDigitsOnly(evt);
        Validation.setLength(evt, mobile.getText(), 10);
    }//GEN-LAST:event_mobileKeyTyped

    private void emailKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_emailKeyTyped
        if (Validation.isValidemail(email.getText())) {
            email.setForeground(new Color(39, 55, 70));
        } else {
            email.setForeground(Color.red);
        }
    }//GEN-LAST:event_emailKeyTyped

    private void credit_periodKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_credit_periodKeyTyped
        Validation.setDigitsOnly(evt);
        Validation.setLength(evt, credit_period.getText(), 3);
    }//GEN-LAST:event_credit_periodKeyTyped

    private void credit_limitKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_credit_limitKeyTyped
        Validation.setDigitsOnly(evt);
        Validation.setLength(evt, credit_limit.getText(), 10);
    }//GEN-LAST:event_credit_limitKeyTyped

    private void credit_limitKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_credit_limitKeyReleased

    }//GEN-LAST:event_credit_limitKeyReleased

    private void sameAsAboveMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_sameAsAboveMouseClicked
        if (sameAsAbove.isSelected()) {
            business_street.setText(street_name.getText());
            business_city.setText(personal_city.getText());
        } else {
            business_street.setText("");
            business_city.setText("");
        }
    }//GEN-LAST:event_sameAsAboveMouseClicked

    private void btn_CancelActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_CancelActionPerformed
        clearall();
        generateID();
    }//GEN-LAST:event_btn_CancelActionPerformed

