    private void generateID() {
        try {
            ResultSet rs = DB.search("SELECT COUNT(id_customer) AS id FROM customer");
            if (rs.next()) {
                int rowcount = rs.getInt("id");
                rowcount++;
                customerID.setText("CUS" + String.format("%0" + 4 + "d", rowcount));
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

